        this.forceUpdate();
		}

		render() {
		    
		    var disp = this.state.buttArr.map(button => <div style={{ padding: 5}}> {button} </div>)
		    console.log(disp);
		    return (
                <div style={{ padding: 20 }}>
			   <Grid container>
               <div style={{ padding: 5 }}>
				 {disp}
               </div>
			   </Grid>
			 </div>
		);
	}
}

export default ClothingButtons;
