import React from 'react';
import Typography from '@material-ui/core/Typography';
import ExistingAccountLookup from './ExistingAccountLookup.js';
import ExistingAccountDeposit from './ExistingAccountDeposit.js';
import Autocomplete from './Autocomplete.js';

class ExistingAccountInput extends React.Component {
    constructor() {
        super();
        this.updateSearchString = this.updateSearchString.bind(this);
        this.getNames = this.getNames.bind(this);
        this.lookupBalance = this.lookupBalance.bind(this);
        this.state = {
            searchString: null,
            accountJson: null,
            names: [],
            dispBalance: null,
        };   
    }

    updateSearchString(str) {
        this.state.searchString = str;
        console.log(this.state.searchString);
    }

    lookupBalance(matchName) {
        this.state.accountJson.forEach(info =>{
            if(info.Name === matchName)
                this.state.dispBalance = info.Balance;
        })
        this.state.dispBalance = 0;
    }

    getNames() {
        console.log(this.state.names);
        return this.state.names;
    }

    componentDidMount() {
        var url = 'http://localhost:3001/RecordsJson'
        fetch(url).then(response => {
            return response.json();
        })
        .then(myJson => {
            console.log(myJson);
            var ret = [];
            myJson.forEach(info =>{
                ret.push(info.Name)
            })
            console.log(ret);
            this.setState({accountJson: myJson, names: ret});
            console.log(this.state.names);
        })
        .catch((error) => {
            console.error(error);
        });
    }

    render() {
        return(
            <div>
			    <Typography gutterBottom variant="h5" component="h2">
				    Existing Account Update
			    </Typography>
                <ExistingAccountLookup update={this.updateSearchString}/>
                <ExistingAccountDeposit/>
                <Autocomplete getNames={this.getNames} suggestions={this.state.names}/>
            </div>
		);
    }
}

export default ExistingAccountInput;
