import React from 'react';
import XLSX from 'xlsx';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';



class SnackButtons extends React.Component {

	constructor(props) {
            super(props);
            this.genButtons = this.genButtons.bind(this);
            this.state = {
                buttArr: this.genButtons(),
            };
        }

        genButtons() {
            const XLSX = require('xlsx');
            //var path = require('./SampleInventory.xlsx');
            //console.log(path);
            //var workbook = XLSX.readFile('./SampleInventory.xlsx');
            //console.log(workbook);
            var arr = [];
            var data = null;
            var url = 'http://localhost:3001/json'
            fetch(url).then(function(response) {
                return response.json();
            })
            .then(function(myJson) {
                data = myJson;
                data.forEach(function (info){
                    console.log(this);
                    console.log(info);
                    var thisButton = <Button style={{backgroundColor:"#8d6e63", color: '#faebd7', fontWeight: 'bold'}} variant='contained' size='large'>{info.Name}</Button>;
                arr.push(thisButton);
            })
                
        })
        console.log(arr);
            return arr;
    }

    render() {
        var disp = this.state.buttArr.map(button => <div style={{ padding: 5}}> {button} </div>)
    console.log(disp);
        return (
			<div style={{ padding: 20 }}>
			   <Grid container>
				 {disp}
			   </Grid>
    </div>
		);
    }
}

export default SnackButtons;
