import React from 'react';
import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import Divider from '@material-ui/core/Divider';


const theme = createMuiTheme({
    palette: {
		primary: {
			main: "#E90C3E"
		},
		secondary: {
			main: "#FAEBD7"
		}
	}
});

// input: array of tuples with name-balance pairs
// 
function extractName(envelope) {
    var tup;
    var output = ""
    for (tup of envelope) {
        output += tup[0];
        output += "\n"
    }
    return output;
} 

function extractBalance(envelope) {
    var tup;
    var output = "$"
    for (tup of envelope) {
        output += tup[1];
        output += "\n"
    }
    return output;
} 
//<ListItemText id={labelId} primary={extractName(value)}/>

export default function EnvelopeList() {
//   const classes = theme;
  const [checked, setChecked] = React.useState([0]);

  var envel_arr = []
  // TODO: make request here
  envel_arr = [[["Christopher Scott", 7.50]],[["Shirley Temple", 0.50], ["Zachary Temple", 2.00]]]

  const handleToggle = (value) => () => {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
  };

  return (
      <MuiThemeProvider theme={theme}>
        <List className={theme}>
        {envel_arr.map((value) => {
            const labelId = `checkbox-list-label-${value}`;
            var total = 0.0;
            var remainder = 0.0;

            return (
            <ListItem key={value} role={undefined} dense button onClick={handleToggle(value)}>
                <ListItemIcon>
                <Checkbox
                    edge="start"
                    checked={checked.indexOf(value) !== -1}
                    tabIndex={-1}
                    disableRipple
                    inputProps={{ 'aria-labelledby': labelId }}
                    color='primary'
                />
                </ListItemIcon>
                <Paper elevation='2' style={{padding:'20px', flexGrow: 1}}>
                    <Grid container spacing={8} style={{padding: '20px'}}>
                        {value.map((individual) =>{
                            total += individual[1];
                            return(
                                <Grid container spacing={2} style={{padding: '8px'}}>
                                    <Grid item sm={5}>
                                        <Typography variant='body1'>{individual[0]}</Typography>
                                    </Grid>
                                    <Grid item sm={2}>
                                        <Typography variant='body2'>${individual[1].toFixed(2)}</Typography>
                                    </Grid>
                                    <Grid item sm={5}/>
                                </Grid>
                            );
                        })}
                    </Grid>
                    <Grid container spacing={2} style={{paddingTop: '4px'}}>
                        <Grid item sm={7}>
                            <Divider variant="middle" style={{marginLeft: '0px', flexGrow: 1}}/>
                        </Grid>
                        <Grid item sm={5}/>
                        <Grid item sm={5}/>
                        <Grid item sm={2}>
                            <Typography variant='body2'>${total.toFixed(2)}</Typography>
                        </Grid>
                        <Grid item sm={1}>
                            {() =>{
                                remainder = total;
                                var num = remainder % 20;
                                remainder -= num * 20;
                                return(
                                    <Typography variant='body2'>{num}</Typography>
                                );
                            }}
                        </Grid>
                    </Grid>
                </Paper>
            </ListItem>
            );
        })}
        </List>
    </MuiThemeProvider>
  );
}
